import numpy as np# Per un campione di dati n-1, per una popolazione nx = np.array([1, 2, 3, 4, 5])# Calcolo della media campionariamedia = np.mean(x)# Calcolo delle deviazioni dalla mediadeviazioni = x - media# Calcolo dei quadrati delle deviazioniquadrati = deviazioni ** 2# Somma dei quadrati delle deviazionisomma_quadrati = np.sum(quadrati)# Calcolo della varianza campionarian = len(x)varianza_campionaria = somma_quadrati / (n - 1)print(f"Media campionaria: {media}")print(f"Varianza campionaria: {varianza_campionaria}")